import React, { Component } from 'react';
import axios from 'axios';

class RandomBeer extends Component {


    constructor(props) {
      super(props);

      this.state = {
        beerz: []
      }
    }

      componentDidMount() {

        axios.get(`https://api.punkapi.com/v2/beers`)
        .then(res => {
          let beersArray = [];
          const beers = res.data;
          beers.map((beer) => {
           beersArray.push(beer);
          var randomIndex = Math.floor((Math.random() * 20) + 1);
          this.setState({ beerz: beersArray[randomIndex] });
        })
      })

    }

    render() {

      console.log(this.state.beer);
        return (

               <div className="cards random">


                     <div className="card" key={this.state.beer.id}>
  <img className="card-img-top" src={this.state.beer.image_url} alt="Card image cap" />
  <div className="card-body">
    <h5 className="card-title">{this.state.beer.name}</h5>
    <p className="card-text">{this.state.beer.description.slice(50, 100)}</p>
    <button className="btn btn-primary">{this.state.beer.tagline}</button>
  </div>
</div>

            </div>
        )
    }
}

export default RandomBeer;
