import React, { Component } from 'react';
import axios from 'axios';

class Beers extends Component {

    state = {
        beers: []
      }

      componentDidMount() {
        axios.get(`https://api.punkapi.com/v2/beers`)
          .then(res => {
            const beers = res.data;
            this.setState({ beers });
          })
          .catch(err => console.log(err));
      }


    render() {
        return (
            <div>
                <h1>beers</h1>
            </div>
        )
    }
}

export default Beers;
