import React, { Component } from 'react';
import './App.css';
import Chart from './components/Chart';

export default class App extends Component {
  state = {
    chartData: props.chartData
  }

  getChartData = () => {
    this.setState({
      chartData: {
        labels: ['Boston', 'Worcester', 'Springfield', 'Lowell', 'Cambridge', 'New Bedford'],
        datasets:[
          {
            label:'Population',
            data:[
              617594,
              181045,
              153060,
              106519,
              105162,
              95072
            ],
            backgroundColor:[
              'rgba(255, 99, 132, 0.6)',
              'rgba(54, 162, 235, 0.6)',
              'rgba(255, 206, 86, 0.6)',
              'rgba(75, 192, 192, 0.6)',
              'rgba(153, 102, 255, 0.6)',
              'rgba(255, 159, 64, 0.6)',
              'rgba(255, 99, 132, 0.6)'
            ]
          }
        ]
      }
    })
  }

  componentWillMount() {
    this.getChartData();
  }

  render() {
    return (
      <div className="App">
  <Chart chartData={this.state.chartData}
  legendPosition="bottom"
  />
      </div>
    );
  }
}
